var lazy = $('').lazyRule((MY_HOME) => {
    var html = request(input, {
        headers: {
            "Cookie": getMyVar('cookie'),
            "Referer": MY_HOME
        },
        method: "get"
    })
    var player = JSON.parse(html.match(/r player_.*?=(.*?)</)[1]);
    var jsurl = player.url;
    var from = player.from;
    if (player.encrypt == '1') {
        var jsurl = unescape(jsurl);
    } else if (player.encrypt == '2') {
        var jsurl = unescape(base64Decode(jsurl));
    } else {
        jsurl
    }
    //log(jsurl)
    eval(fetch(MY_HOME + "/static/js/playerconfig.js"));
    var jx = MacPlayerConfig.player_list[from].parse;
    if (jx == '') {
        jx = MacPlayerConfig.parse;
    }
    if (jx == '') {
        var pshtml = fetch(MY_HOME + "/static/player/" + from + ".js");
        jx = pdfh(pshtml, "iframe&&src").split("=")[0] + "=";
    }
    //log(jx)
    try {
        if (/\.m3u8|\.mp4/.test(jsurl)) {
            return jsurl
        } else {
            eval((request(jx + jsurl).match(/var config = {[\s\S]*?}/)[0]));
            var body = "url=" + config.url + "&time=" + config.time + "&key=" + config.key;
            var link = jx.split("?")[0] + "api_config.php";
            var video = post(link, {
                headers: {
                    "x-requested-with": "XMLHttpRequest",
                    "User-Agent": "Mozilla/5.0 (Linux; Android 5.1.1; GM1910 Build/LMY48G; wv) AppleWebKit/537.36 (KHTML, like Gecko) Version/4.0 Chrome/52.0.2743.100 Mobile Safari/537.36",
                    "content-type": "application/x-www-form-urlencoded"
                },
                body: body
            });
            let playUrl = JSON.parse(video).url;
            //log(playUrl)
            return playUrl + "#isVideo=true#";
        }
    } catch (e) {
        return 'video://' + input;
    }
}, MY_HOME);
