function ASE(data) {
    eval(getCryptoJS());
    var key = CryptoJS.enc.Utf8.parse('0123456789123456');
    var iv = CryptoJS.enc.Utf8.parse('2015030120123456');
    var decrypted = CryptoJS.AES.decrypt(data, key, {
        iv: iv,
        mode: CryptoJS.mode.CBC,
        padding: CryptoJS.pad.Pkcs7
    });
    return decrypted.toString(CryptoJS.enc.Utf8);
}

function DES(text) {
    eval(getCryptoJS());
    var key = CryptoJS.enc.Utf8.parse('ZeYw3vSZhVcG6mTxNZqG2Pb8');
    //log(key)
    var iv = CryptoJS.enc.Utf8.parse('01234567');   
    // var WordArray = CryptoJS.enc.Hex.parse(text);  // å¦‚æœtextæ˜¯base64å½¢å¼ï¼Œè¯¥è¡Œæ³¨é‡Šæ‰
    // var text = CryptoJS.enc.Base64.stringify(WordArray);  // å¦‚æœtextæ˜¯base64å½¢å¼ï¼Œè¯¥è¡Œæ³¨é‡Šæ‰
    var a = CryptoJS.DES.decrypt(text, key, {
        mode: CryptoJS.mode.CBC,
        padding: CryptoJS.pad.Pkcs7,
        iv: iv
    });
    return CryptoJS.enc.Utf8.stringify(a).toString()
}
