    function revolution(text, s4p) {
        var encrypted = CryptoJS.AES.decrypt(text, CryptoJS.enc.Utf8.parse(s4p), {
            iv: CryptoJS.enc.Utf8.parse('1234567890123456'),
            mode: CryptoJS.mode.CBC,
            padding: CryptoJS.pad.Pkcs7
        });
        return encrypted.toString(CryptoJS.enc.Utf8);
    }

    var jsurl = revolution(input, tk + '')

    if (jsurl.match(/xiwood|gazes/)) {
        url = cacheM3u8(jsurl)
