        for (var i = 0; i < arrBytes.length; i++) {Â Â Â Â 
            var tmp;Â Â Â 
            var num = arrBytes[i];Â Â Â Â 
            if (num < 0) {Â Â Â Â  //æ­¤å¤„å¡«å‘ï¼Œå½“byteå› ä¸ºç¬¦åˆä½å¯¼è‡´æ•°å€¼ä¸ºè´Ÿæ—¶å€™ï¼Œéœ€è¦å¯¹æ•°æ®è¿›è¡Œå¤„ç†
                Â Â Â Â Â Â 
                tmp = (255 + num + 1).toString(16);Â Â Â Â 
            } else {Â Â Â Â Â Â 
                tmp = num.toString(16);Â Â Â Â 
            }Â Â Â Â 
            if (tmp.length == 1) {Â Â Â Â Â Â 
                tmp = "0" + tmp;Â Â Â Â 
            }Â Â Â Â 
            str += tmp;Â Â 
        }Â Â 
        return str;
    }

    function aesTemplate(data, key, iv, mode, isEncrypt, isHex) {
        var test = loadJavaClass('hiker://files/cache/bidi.dex', 'com.rule.NumConvertUtil');

        if (!mode) {
            mode = "AES/CBC/PKCS5Padding"
        }

        if (/ECB/.test(mode)) {
            try {
                var keySpec = new SecretKeySpec(String(key).getBytes(), "AES");
                var cipher = Cipher.getInstance(mode);
                cipher.init(isEncrypt ? 1 : 2, keySpec);
                if (isEncrypt) {
                    if (isHex) {
                        return Tool.Bytes2HexString(cipher.doFinal(String(data).getBytes()));
                    } else {
                        return _base64.encodeToString(cipher.doFinal(String(data).getBytes()), _base64.NO_WRAP);
                    }
                } else {
                    if (isHex) {
                        return String(cipher.doFinal(test.hexStringToByte(data)));
                    } else {
                        return String(cipher.doFinal(_base64.decode(data, _base64.NO_WRAP)));
                    }
                }
            } catch (e) {
                return "" + e;
            }

        } else {
            try {
                var keySpec = new SecretKeySpec(String(key).getBytes(), "AES");
                var cipher = Cipher.getInstance(mode);
                //å‘é‡iv
                var ivParameterSpec = new IvParameterSpec(String(iv).getBytes());
                cipher.init(isEncrypt ? 1 : 2, keySpec, ivParameterSpec);
                if (isEncrypt) {
                    if (isHex) {
                        return Tool.Bytes2HexString(cipher.doFinal(String(data).getBytes()));
                    } else {
                        return _base64.encodeToString(cipher.doFinal(String(data).getBytes()), _base64.NO_WRAP);
                    }
                } else {
                    if (isHex) {
                        return String(cipher.doFinal(test.hexStringToByte(data)));
                    } else {
                        return String(cipher.doFinal(_base64.decode(data, _base64.NO_WRAP)));
                    }
                }

            } catch (e) {
                return "" + e;
            }
        }
    }
}

