            if (playdata[i].type == "M4A_128") {
                return De(playdata[i].url)
            }else if (playdata[i].type == "M4A_64") {
                return De(playdata[i].url)
            }
        }
        */

    } catch (e) {
        return "toast://ä»˜è´¹å†…å®¹ï¼Œæ— æ³•æ’­æ”¾ï¼"
    }

});"},{"col_type":"movie_3","name":"å¤‡ç”¨æ–¹æ³•","path":"xmly_BYFunctions","rule":"var MyFunctions = {
